// Clean up
$text = trim($raw_text);
// Remove extra spaces
$text = preg_replace('/\s+/', ' ', $text);

// Split by spaces
$words = explode(' ', $text);

// Shuffle words to make it random practice
shuffle($words);

// Chunk into 60 words
$chunks = array_chunk($words, 10);

$stmt = $pdo->prepare("INSERT INTO paragraphs (language, difficulty, title, content) VALUES (:language, :difficulty, :title, :content)");

$count = 0;
foreach ($chunks as $index => $chunk) {
    $content = implode(" ", $chunk);
    $title = "English Practice " . ($index + 1);
    
    // Check if duplicate content exists to avoid spamming db if run multiple times
    // (Optional, but good practice. For now just insert)
    
    $stmt->execute([
        ':language' => 'english',
        ':difficulty' => 'medium',
        ':title' => $title,
        ':content' => $content
    ]);
    $count++;
}

echo "Inserted $count English paragraphs successfully.";
